mapper.select:
SELECT


FROM repair_order AS ro
JOIN elevator AS e ON e.elevator_id = ro.elevator_id
JOIN unit u ON e.unit_id = u.unit_id
JOIN region r ON r.region_id = e.region_id


        AND ro.id = #{id,jdbcType=INTEGER}


        AND order_id = #{orderId,jdbcType=VARCHAR}


        AND ro.elevator_id = #{elevatorId,jdbcType=VARCHAR}


        AND ro.terminal_id = #{terminalId,jdbcType=VARCHAR}


        AND reporter_id = #{reporterId,jdbcType=VARCHAR}


        AND company_id = #{companyId,jdbcType=VARCHAR}


        AND company_name = #{companyName,jdbcType=VARCHAR}


        AND group_id = #{groupId,jdbcType=VARCHAR}


        AND group_name = #{groupName,jdbcType=VARCHAR}


        AND description = #{description,jdbcType=VARCHAR}


        AND reason = #{reason,jdbcType=VARCHAR}


        AND ro.type = #{type,jdbcType=VARCHAR}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND create_time > #{createStartTime,jdbcType=TIMESTAMP}
        AND create_time < #{createEndTime,jdbcType=TIMESTAMP}


        AND repair_time = #{repairTime,jdbcType=TIMESTAMP}


        AND repair_time > #{repairStartTime,jdbcType=TIMESTAMP}
        AND repair_time < #{repairEndTime,jdbcType=TIMESTAMP}


        AND confirm_time = #{confirmTime,jdbcType=TIMESTAMP}


        AND confirm_time > #{confirmStartTime,jdbcType=TIMESTAMP}
        AND confirm_time < #{confirmEndTime,jdbcType=TIMESTAMP}


        AND remark = #{remark,jdbcType=VARCHAR}


        AND ro.state = #{state,jdbcType=INTEGER}


        AND is_misreport = #{misreport,jdbcType=BOOLEAN}


        AND province_id = #{provinceId,jdbcType=VARCHAR}


        AND city_id = #{cityId,jdbcType=VARCHAR}


        AND district_id = #{districtId,jdbcType=VARCHAR}


        AND town_id = #{townId,jdbcType=VARCHAR}
