mapper.select:
SELECT


FROM trap_order AS t
JOIN elevator AS e ON e.elevator_id = t.elevator_id
JOIN unit u ON e.unit_id = u.unit_id
JOIN region r ON r.region_id = e.region_id


        AND t.id = #{id,jdbcType=INTEGER}


        AND order_id = #{orderId,jdbcType=VARCHAR}


        AND t.elevator_id = #{elevatorId,jdbcType=VARCHAR}


        AND t.terminal_id = #{terminalId,jdbcType=VARCHAR}


        AND reporter_id = #{reporterId,jdbcType=INTEGER}


        AND company_id = #{companyId,jdbcType=INTEGER}


        AND company_name = #{companyName,jdbcType=VARCHAR}


        AND group_id = #{groupId,jdbcType=INTEGER}


        AND group_name = #{groupName,jdbcType=VARCHAR}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND create_time > #{createStartTime,jdbcType=TIMESTAMP}
        AND create_time < #{createEndTime,jdbcType=TIMESTAMP}


        AND trap_time = #{trapTime,jdbcType=TIMESTAMP}


        AND trap_time > #{trapStartTime,jdbcType=TIMESTAMP}
        AND trap_time < #{trapEndTime,jdbcType=TIMESTAMP}


        AND receive_time = #{receiveTime,jdbcType=TIMESTAMP}


        AND receive_time > #{receiveStartTime,jdbcType=TIMESTAMP}
        AND receive_time < #{receiveEndTime,jdbcType=TIMESTAMP}


        AND arrive_time = #{arriveTime,jdbcType=TIMESTAMP}


        AND arrive_time > #{arriveStartTime,jdbcType=TIMESTAMP}
        AND arrive_time < #{arriveEndTime,jdbcType=TIMESTAMP}


        AND finish_time = #{finishTime,jdbcType=TIMESTAMP}


        AND finish_time > #{finishStartTime,jdbcType=TIMESTAMP}
        AND finish_time < #{finishEndTime,jdbcType=TIMESTAMP}


        AND duration = #{duration,jdbcType=INTEGER}


        AND is_misreport = #{misreport,jdbcType=BOOLEAN}


        AND t.state = #{state,jdbcType=INTEGER}


        AND province_id = #{provinceId,jdbcType=VARCHAR}


        AND city_id = #{cityId,jdbcType=VARCHAR}


        AND district_id = #{districtId,jdbcType=VARCHAR}


        AND town_id = #{townId,jdbcType=VARCHAR}
